package com.exercise.week_eight;/** * @ClassName: * @Description: * @Author: * @Date: */import java.util.Arrays;/** *@ClassName LeetCode_300_0340最长上升子序列 *@Description *@Autor HDR *@Date 2020/2/1621:50 *@Version 1.0 *@Example **/public class LeetCode_300_0340最长上升子序列 {    public int lengthOfLIS(int[] nums) {        if(nums.length == 0){            return 0;        }        int[] dp = new int[nums.length];        int res = 0;        Arrays.fill(dp, 1);        for(int i = 0; i < nums.length; i++) {            for(int j = 0; j < i; j++) {                if(nums[j] < nums[i]) dp[i] = Math.max(dp[i], dp[j] + 1);            }            res = Math.max(res, dp[i]);        }        return res;    }}